create table if not exists  hist_search (id bigint generated by default as identity, keyword varchar(255) not null, search_time timestamp, username varchar(255) not null, primary key (id));
create table if not exists users (username varchar(255) not null, is_account_non_expired boolean, is_account_non_locked boolean, is_credentials_non_expired boolean, is_enabled boolean, password varchar(255) not null, primary key (username));
create table if not exists authority (username varchar(255) not null,  authority_name varchar(255) not null, primary key (username));